var tabGroupMain = Titanium.UI.createTabGroup();
 
//
// create base UI tab and root window
//
 
var winMain = Titanium.UI.createWindow({
    backgroundColor: 'orange',
    title: 'Red Window'
});
 
var win1 = Titanium.UI.iOS.createNavigationWindow({
   window: winMain,
    title: 'Tab 1',
    backgroundColor: 'orange',
    layout: 'vertical'
    
});
 
var tabFirst = Titanium.UI.createTab({
    icon: 'KS_nav_views.png',
    title: 'Tab 1',
    window: win1
});
 
 
// Create a TableView.
var aTableView = Ti.UI.createTableView();
 
// Populate the TableView data.
var data = [
	{title:'Row 1', hasChild:true, color:'red', header:'First'},
	{title:'Row 2', hasDetail:true, color:'green'},
	{title:'Row 3', hasCheck:true, color:'blue', header:'Second'},
	{title:'Row 4', color:'orange'}
];
aTableView.setData(data);
 
// Listen for click events.
 
win1.add(aTableView);
 
 
var close = Titanium.UI.createButton({
    color: 'black',
    title: 'Back',
    width: 180,
    height: 35,
    bottom:10
});
 
win1.add(close);
 
close.addEventListener('click', function(){
     tabGroup.close();
     
     
  
});
 
aTableView.addEventListener('click',
function(e) {
 
    var tabWin = Titanium.UI.createWindow({
        title: 'Modal Window',
        backgroundColor: '#f0f',
        width: '100%',
        height: '100%',
        tabBarHidden: true
    });
    
       var tBtn = Titanium.UI.createLabel({
        text: 'Hello World',
        width: 180,
        height: 35
    });
    tabWin.add(tBtn);
 
    var tabGroup = Titanium.UI.createTabGroup({
        bottom: -500,
        width: '100%',
        height: '100%'
    });
    var tab1 = Titanium.UI.createTab({
        icon: 'KS_nav_views.png',
        width: '100%',
        height: '100%',
        title: 'tabWin',
        window: tabWin
    });
    tabGroup.addTab(tab1);
    tabGroup.open();
 
    var closeBtn = Titanium.UI.createButton({
        title: 'Back'
    });
    tabWin.leftNavButton = closeBtn;
    closeBtn.addEventListener('click',
    function(e) {
        tabGroup.animate({
            duration: 400,
            bottom: -500
        },
        function() {
            tabGroup.close();
        });
    });
 
 
 
 
  
 
 
