XML
<View class="labelOuterContainer"  id="bedroomsContainer">
	<View class="labelInnerContainer" id="bedroomsWrapper">
		<Label id="bedrooms" class="headerLabel" />
		<Label id="bedroomsCount" class="contentLabel" />
	</View>
	<View class="innerView"/>
</View>


TSS 


".labelOuterContainer":{
  height: Ti.UI.SIZE,
  width: Ti.UI.SIZE,
  layout: "horizontal",
  top: 10
}

".labelInnerContainer":{
  height: Ti.UI.SIZE,
  width: Ti.UI.SIZE,
  layout: "vertical"
}

".headerLabel":{
  color: Alloy.Globals.Colors.WHITE,
  left: 0,
  font:{
    fontSize: 10
  },
  wordWrap: false,
  ellipsize: true,
  width:Ti.UI.SIZE
}

".headerLabel[formFactor=tablet]":{
  font:{
    fontSize: 16
  }
}

".contentLabel":{
  color: Alloy.Globals.Colors.WHITE,
  top: 3,
  left: 0,
  font:{
    fontSize: 14
  },
  ellipsize: true,
  width:Ti.UI.SIZE
}

".contentLabel[formFactor=tablet]":{
  font:{
    fontSize: 20
  }
}

".headerLabel[platform=ios formFactor=handheld]":{
  minimumFontSize: 10,
  height: 14,
  ellipsize: false
}

".headerLabel[platform=ios formFactor=tablet]":{
  minimumFontSize: 16,
  height: 20,
  ellipsize: false
}


".contentLabel[platform=ios formFactor=handheld]":{
  minimumFontSize: 14,
  height: 18,
  ellipsize: false
}

".contentLabel[platform=ios formFactor=tablet]":{
  minimumFontSize: 20,
  height: 24,
  ellipsize: false
}

".innerView":{
  width: 20,
  height: Ti.UI.SIZE
}
