var win = Ti.UI.createWindow();

var titleView = Ti.UI.createTextArea({
	top:0,
	left:10,
	right:10,
   	attributedHintText:Ti.UI.createAttributedString({
	    text:'Place',
		attributes:[{
			type:Ti.UI.ATTRIBUTE_FOREGROUND_COLOR,
  			value:'#AAA',
  			range:[0, ('Place').length]
  		}]
	}),
	color:'#808080'
});

win.add(titleView);

win.open();
