var win = Ti.UI.createWindow({
	backgroundColor:"white",
	title:"Window1"
})

var scrollView = Ti.UI.createScrollView({
	backgroundColor:"white",
});

//Opaque BG
var myView1 = Ti.UI.createView({
    width: 100,
    height: 100,
    top:10,
    backgroundColor:"#ff00",
    borderWidth: 5,
    borderColor: "green",
    borderRadius:10
});

//Semi transparent BG
var myView2 = Ti.UI.createView({
    width: 100,
    height: 100,
    top:120,
    backgroundColor:"#af00",
    borderWidth: 5,
    borderColor: "green",
    borderRadius:10
});

//Transparent Border, Semitransparent BG
var myView3 = Ti.UI.createView({
    width: 100,
    height: 100,
    top:230,
    backgroundColor:"#af00",
    borderWidth: 5,
    borderColor: "#0000",
    borderRadius:10
});

//NO BG
var myView4 = Ti.UI.createView({
    width: 100,
    height: 100,
    top:340,
    borderWidth: 5,
    borderColor: "green",
    borderRadius:10
});

//Transparent BG
var myView5 = Ti.UI.createView({
    width: 100,
    height: 100,
    top:450,
    borderWidth: 5,
    borderColor: "green",
    borderRadius:10,
    backgroundImage:"transparent.png"
});
//ImageView with single pixel border
var imageView1 = Titanium.UI.createImageView({
    url: "KS_nav_ui.png",
    width: 100,
    height: 100,
    top: 560,
    borderWidth: 1,
    borderColor: "green",
    borderRadius: 5
});
//ImageView with fat border
var imageView2 = Titanium.UI.createImageView({
    url: "KS_nav_ui.png",
    width: 100,
    height: 100,
    top: 670,
    borderWidth: 5,
    borderColor: "green",
    borderRadius: 5
});
//ImageView with transparent border
var imageView3 = Titanium.UI.createImageView({
    url: "KS_nav_ui.png",
    width: 100,
    height: 100,
    top: 780,
    borderWidth: 5,
    borderColor: "#0000",
    borderRadius: 5
});
scrollView.add(myView1);
scrollView.add(myView2);
scrollView.add(myView3);
scrollView.add(myView4);
scrollView.add(myView5);
scrollView.add(imageView1);
scrollView.add(imageView2);
scrollView.add(imageView3);


win.add(scrollView);
win.open();
{code}
