h2. Test Case

{code}
var win = Ti.UI.createWindow({
	backgroundColor: '#fff',
	fullscreen: false,
	exitOnClose: true
});

var view = Ti.UI.createView({
	backgroundColor: '#a00',
	height:200,
	width:200
});
win.add(view);

view.addEventListener('swipe', function(e) {
	alert(e.direction);
});

win.open();
{code}

