function createSectionForGroup(group) {
    var headerContainerOpts, headerBottomBorderOpts, headerViewOpts;
 
    headerViewOpts = {
        color: 'white',
        height: Ti.UI.SIZE,
        font: { fontSize: '17sp', fontWeight: 'bold' },
        left: 11,
        text: group
    };
    
    if (group === RED) {
        headerContainerOpts = {
            backgroundGradient: {
                endPoint: { x: 0, y: '100%' },
                startPoint: { x: 0, y: 0 },
                type: 'linear',
                colors: ['#C53019', '#B32D14']
            },
            height: 32
        };
    }
    if (group === GREEN) {
        headerContainerOpts = {
            backgroundGradient: {
                endPoint: { x: 0, y: '100%' },
                startPoint: { x: 0, y: 0 },
                type: 'linear',
                colors: ['#2B781E', '#195010']
            },
            height: 32
        };
    }
    if (group === PURPLE) {
        headerContainerOpts = {
            backgroundGradient: {
                endPoint: { x: 0, y: '100%' },
                startPoint: { x: 0, y: 0 },
                type: 'linear',
                colors: ['#873DA6', '#612A7D']
            },
            height: 32
        };
    }
 
    var container = Ti.UI.createView(headerContainerOpts);
    container.add(Ti.UI.createLabel(headerViewOpts));
    return Ti.UI.createTableViewSection({
        headerView: container,
        title: group
    });
}
