    min: 0,
    max: 10 
});

var dismissButtonToolBar3 = Ti.UI.createButton({
    title: "OK",
    style: Ti.UI.iPhone.SystemButtonStyle.DONE
});

dismissButtonToolBar3.addEventListener("click", function() {
