var file, filename, logoFile, htmlContent, docViewer;

file = Ti.Filesystem.createTempFile();
filename = "test.html";

logoFile = Ti.Filesystem.getFile(Ti.Filesystem.resourcesDirectory + 'images/logo.png');
htmlContent = '<html><body>test<img src="data:image/gif;base64,' + Ti.Utils.base64encode(logoFile.read()) + '" />test</body></html>';
file.write(htmlContent);
file.rename(filename);

docViewer = Ti.UI.iOS.createDocumentViewer({'url':Ti.Filesystem.tempDirectory + filename});
docViewer.addEventListener('unload', function(){
	Ti.Filesystem.getFile(Ti.Filesystem.tempDirectory + filename).deleteFile();
});
docViewer.show();
