h3. Test case
{code}
var win = Ti.UI.createWindow();
var btn = Ti.UI.createButton({
	title: 'here'
});
btn.addEventListener('click', function(){
	var xhr = Ti.Network.createHTTPClient();
	xhr.onload = function() {
		Ti.API.info('--------------------');
		Ti.API.info(this.getAllResponseHeaders());
		Ti.API.info('--------------------');
	};
	xhr.open('GET', 'http://google.com');
	xhr.send();
});
win.add(btn);
win.open();
{code}
