var win = Titanium.UI.createWindow({  
	layout:'vertical',
    backgroundColor:'#fff'
});

var createSection = function(title,rowCount){
	var s1 = Titanium.UI.createTableViewSection({
		headerTitle:title
	});
	
	for (i=0;i<rowCount;i++)
	{
		var row = Ti.UI.createTableViewRow({
			title:'Row '+i+' of '+title
		})
		s1.add(row);
	}
	return s1;
}

var tblView = Ti.UI.createTableView({})

var data = [];
data.push(createSection('One',5));
data.push(createSection('Two',15));
data.push(createSection('Three',25));
tblView.setData(data);


var wrapper = Ti.UI.createView({
	height:Ti.UI.SIZE,
	layout:'horizontal'
})

var input = Ti.UI.createTextField({
	left:'5%',
	width:'40%',
	top:5,
	bottom:5,
	height:40,
	borderStyle: Ti.UI.INPUT_BORDERSTYLE_BEZEL,
	keyboardType:Ti.UI.KEYBOARD_NUMBER_PAD,
})

wrapper.add(input);

var btn = Ti.UI.createButton({
	left:'5%',
	width:'40%',
	top:5,
	bottom:5,
	height:40,
	title:'Select'
})

btn.addEventListener('click',function(){
	input.blur();
	var val = input.value;
	if(val && val.length > 0)
	{
		tblView.selectRow(val);
	}
})

wrapper.add(btn);

win.add(wrapper);


win.add(tblView);


win.open();
