            id:'password',
            backgroundColor:'#eeeeee',
            returnKeyType:Titanium.UI.RETURNKEY_DONE,
            enableReturnKey:true,
            keyboardType:Titanium.UI.KEYBOARD,
            autocorrect:false,
            hintText:'Password',
            passwordMask:true, 
            textAlign:'left',
            clearOnEdit:true,
            borderStyle:Titanium.UI.INPUT_BORDERSTYLE_BEZEL,
