comments.body:
PR: https://github.com/appcelerator/titanium_mobile/pull/7961
Using {{Ti.UI.Window.close()}} or device back should close with transition. 

{code}
Ti.UI.setBackgroundColor('#000');
var win = Ti.UI.createWindow({
  backgroundColor: 'black',
  fullscreen: false,
  title: 'Master'
});
 
var IMG_BASE = 'https://github.com/appcelerator/titanium_mobile/raw/master/demos/KitchenSink/Resources/images/';
var defaultFontSize = Ti.Platform.name === 'android' ? 16 : 14;
 
var tableData = [];
 
for (var i=1; i<=20; i++){
  var row = Ti.UI.createTableViewRow({
    className:'forumEvent', // used to improve table performance
    selectedBackgroundColor:'white',
    rowIndex:i, // custom property, useful for determining the row during events
    height:110
  });
 
  var avatar = createAvatar();
  avatar.transitionName = "avatar";
  row.add(avatar);
   
  var labelUserName = Ti.UI.createLabel({
    color:'#576996',
    font:{fontFamily:'Arial', fontSize:defaultFontSize+6, fontWeight:'bold'},
    text:'Fred Smith ' + i,
    left:70, top: 6,
    width:200, height: 30,
  });
  row.add(labelUserName);
 
  var labelDetails = Ti.UI.createLabel({
    color:'#222',
    font:{fontFamily:'Arial', fontSize:defaultFontSize+2, fontWeight:'normal'},
    text:'Replied to post with id ' + randomInt(1000) + '.',
    left:70, top:44,
    width:360
  });
  row.add(labelDetails);
 
  var imageCalendar = Ti.UI.createImageView({
    image:IMG_BASE + 'custom_tableview/eventsButton.png',
    left:70, bottom: 2,
    width:32, height: 32
  });
  row.add(imageCalendar);
 
  var labelDate = Ti.UI.createLabel({
    color:'#999',
    font:{fontFamily:'Arial', fontSize:defaultFontSize, fontWeight:'normal'},
    text:'on ' + randomInt(30) + ' Nov 2012',
    left:105, bottom:10,
    width:200, height:20
  });
  row.add(labelDate);
 
  tableData.push(row);
}
 
var tableView = Ti.UI.createTableView({
  backgroundColor:'white',
  data:tableData
});
 
tableView.addEventListener('click', function(e){
	var masterAvatar = e.row.children[0];
	var masterUsername = e.row.children[1];
	
	var win2 = Ti.UI.createWindow({
	animated: true,
	  layout: 'vertical',
	  backgroundColor: 'white',
	  title: 'Details-',
	  activityEnterAnimation: Ti.Android.R.anim.fade_in,
      activityExitAnimation: Ti.Android.R.anim.fade_out
	});
	
	var avatar = createAvatar();
	avatar.width = 80;
	avatar.height = 80;
	avatar.left = 100;
	avatar.transitionName = "avatar";
  	var labelUserName2 = Ti.UI.createLabel({
	    color:'#576996',
	    font:{fontFamily:'Arial', fontSize:defaultFontSize+6, fontWeight:'bold'},
	    text:'Tap to close',
	    top: 20,
	    width:250, height: 60,
	    transitionName: "username",
	  });
	labelUserName2.addEventListener('click', function() {
		win2.close()
	});
	  
	win2.add(avatar)  
    win2.add(labelUserName2);
	win2.addSharedElement(masterAvatar, "avatar");
	win2.addSharedElement(masterUsername, "username");
	win2.open(); 
});
win.add(tableView);
win.open();
 
function createAvatar() {
  var avatar = Ti.UI.createView({
    backgroundColor: 'green',
    left:10, top:5,
    width:50, height:50,
  });
  return avatar; 
}

// generate random number, used to make each row appear distinct for this example
function randomInt(max){
  return Math.floor(Math.random() * max) + 1;
}
{code}
