fields.description:
Implement {{borderRadius}} for {{ProgressBar}}

{code}
var win = Ti.UI.createWindow({ backgroundColor: 'purple' }),
    progressBar = Ti.UI.createProgressBar({
        borderWidth: 5,
        borderColor: 'yellow',
        borderRadius: 5,
        min: 0,
        max: 10,
        value: 0,
        message: 'Downloading 0 of 10',
        width: Ti.UI.FILL
    });

win.add(progressBar)
win.open();
{code}
