Demo:
{code:javascript}
var win = Ti.UI.createWindow({
    backgroundColor: "#fff"
});
var btn = Ti.UI.createButton({
    title: "Clear data"
});

btn.addEventListener("click", function() {
    Ti.UI.Clipboard.clearData()
});

win.add(btn);
win.open();
{code}
