var win = Ti.UI.createWindow({ backgroundColor: 'green' }),
    Button = require('Windows.UI.Xaml.Controls.Button'),
    button = new Button();

button.Content = "PUSH";
button.addEventListener('Tapped', function (e) {
    alert('Tapped');
});
button.addEventListener('Tapped', function (e) {
    Ti.API.info('This should not cause compile error');
});

win.add(button);
win.open();
