PR : https://github.com/appcelerator/titanium_mobile/pull/9622

Test Case 1 (TiUIListView) -

{code:java}
var rows = [];
for (var i = 0; i < 20; i++) {
    rows.push({ properties: { title: 'Row '+ i , backgroundColor: 'red', searchableText:'Row '+i}});
}
var win = Ti.UI.createWindow({
  title: 'TEST',
  backgroundColor: 'white',
  layout: 'vertical'
}); 
var sb = Ti.UI.createSearchBar();
 
var ls = Ti.UI.createListSection({
  items: rows
});
 var lv = Ti.UI.createListView({
    sections: [ls],
    searchView: sb,
    left: 30,
    right: 20
});
 
sb.setHintText("test");
 sb.addEventListener('change', function(e){
  view.setHeight(50);
  Ti.API.info(e.value);
});
 //when the return key is hit, remove focus from our searchBar
sb.addEventListener('return', function(e){
    view.setHeight(0);
    sb.blur();
});
sb.addEventListener('cancel', function(e){
    view.setHeight(0);
});
lv.addEventListener('itemclick', function(e) {
    Ti.API.info('click at index: ' + e.itemIndex);
});

var view = Ti.UI.createView({
  backgroundColor : 'blue',
  height : 0,
});
 view.addEventListener('click', function(e){
    Ti.API.info('view clicked');
  });
win.add(view);
win.add(lv);
win.open();
{code}

Test Case 2 (TiUITableView) -

{code:java}
var rows = [];
for (var i = 0; i < 10; i++) {
    rows.push({ title: 'Row '+ i});
}

var win = Ti.UI.createWindow({
  backgroundColor: 'red',
  layout : 'vertical'
});
  
var sb = Ti.UI.createSearchBar();
var lv = Ti.UI.createTableView({
    data: rows,
    search: sb,
    top : 50,
    left : 30
});
 
 sb.setHintText("test");
 sb.addEventListener('change', function(e){
  Ti.API.info(e.value);
  view.setHeight(50);
});
 
sb.addEventListener('return', function(e){
sb.blur();
view.setHeight(0);
});
 
sb.addEventListener('cancel', function(e){
      view.setHeight(0);
});
lv.addEventListener('click', function(e) {
    Ti.API.info('click at index: ' + e.index);
    Ti.API.info('clicked row data: ' + e.rowData.title);
 
})
 
 var view = Ti.UI.createView({
  backgroundColor : 'blue',
  height : 0,
});
 view.addEventListener('click', function(e){
    Ti.API.info('view clicked');
  });
win.add(view);
win.add(lv);
win.open();

{code}

Test Case 3 (Offset in ListView):
{code:js}
var rows = [];
for (var i = 0; i < 20; i++) {
    rows.push({ properties: { title: 'Row '+ i , backgroundColor: 'red', searchableText:'Row '+i}});
}
var win = Ti.UI.createWindow({
  backgroundColor: 'white'
}); 
var sb = Ti.UI.createSearchBar();
 
var ls = Ti.UI.createListSection({
  items: rows
});
 
var lv = Ti.UI.createListView({
  sections: [ls],
  searchView: sb,
  top: 100,
  left: 30,
  right: 20
});
 
sb.addEventListener('change', function(e){
  Ti.API.info(e.value);
});

// when the return key is hit, remove focus from our searchBar
sb.addEventListener('return', function(e){
    sb.blur();
});

lv.addEventListener('itemclick', function(e) {
    Ti.API.info('click at index: ' + e.itemIndex);
});
 
win.add(lv);
win.open();
{code}
