Test-Case:
{code:js}
var window = Ti.UI.createWindow(),
    webView = Ti.UI.createWebView({
        url: 'https://india.gov.in/website-ministry-commerce-and-industry',
        onlink: function(e) {
            if (e.url.endsWith('.pdf')) {
                alert('PDF: ' + e.url);
                return false;
            }
            return true;
        }
    });

window.add(webView);
window.open();
{code}
