{code}

var win = Ti.UI.createWindow({
	backgroundColor:'white'
})

var sv = Ti.UI.createScrollView({
	contentHeight:'auto',
	layout:'vertical'
})

var data = [];

for (var i = 0; i < 2; i++) 
{
    data[i] = Ti.UI.createTableViewSection({
        headerTitle: 'Section ' + i
    });
    var maxRows = i*2 + 1;
    for (var j=0;j<maxRows;j++)
    {
    	var row = Ti.UI.createTableViewRow({
            className: 'row',
            sectionIndex: i,
            rowIndex: j
        });
        
        var container = Ti.UI.createView({
            layout: 'vertical',
            height: Ti.UI.SIZE,
            top: 10,
            left: 10,
            bottom: 10,
            right: 10
        });
        
        container.add(Ti.UI.createLabel({
            left: 0,
            text: 'Label ' + j,
            font: {
                fontWeight: 'bold',
                fontSize: 16
            }
        }));
         
        container.add(Ti.UI.createLabel({
            top: 5,
            left: 0,
            text: 'Sub Label ' + j,
            font: {
                fontStyle: 'italic',
                fontSize: 14
            }
        }));
        
        container.add(Ti.UI.createTextField({
        	top:5,
        	bottom:5,
        	width:150,
        	height:40,
        	borderStyle:Ti.UI.INPUT_BORDERSTYLE_LINE
        }));
        row.add(container);
        
        data[i].add(row);
    }
}

var tv = Ti.UI.createTableView({
	data:data,
	height:500
})

sv.add(tv);

var btn = Ti.UI.createButton({
	title:'Random Button',
	top:5,
	bottom:5,
	width:130,
	heigh:40
})

sv.add(btn);

win.add(sv);

win.open();

{code}
