comments.body:
h2. Problem
_Android Debugger crash: opening a child window from a parent._

h3. Reproduction

{code:title=app.js}
var myt={};
Ti.include('page1.js','page2.js','page3.js','framework.js');
var winConfig = {
		backgroundColor:'red',
		navBarHidden:true,
		fullscreen:false
};
var wPage = myt.page1Win(winConfig);
wPage.open();
{code}

{code:title=page1.js}

myt.page1Win=function(winConfig){
	var win = devkit.makeWindow(winConfig);
	var b1 = Titanium.UI.createButton({
		title:'Press Me I error in debug mode',
		height:100,
		width:200,
		top:10
	});
	win.add(b1);
	b1.addEventListener('click', function(){
	       var winConfig = {
			backgroundColor:'yellow',
			navBarHidden:true,
			fullscreen:false
		};
        /* STEP 1: Place a breakpoint on the next line */
        /* STEP 2: Debug the app, and step over each line one at a time until "STEP 3" */
		var wPage = myt.page2Win(winConfig);
        /* STEP 3: Step one more time over the next line, and the debugger should crash! */
		wPage.open();
	});
	return win;
};
{code}

{code:title=page2.js|borderStyle=solid}
myt.page2Win=function(winConfig){
	var win = devkit.makeWindow(winConfig);
	var b1 = Titanium.UI.createButton({
		title:'Press Me',
		height:40,
		width:200,
		top:10
	});
	win.add(b1);
	b1.addEventListener('click', function(){
		var winConfig = {
			backgroundColor:'blue',
			navBarHidden:true,
			fullscreen:false
		};
		var wPage = myt.page3Win(winConfig);
		wPage.open();
	});
	return win;
};
{code}

{code:title=page3.js|borderStyle=solid}
myt.page3Win=function(winConfig){
	var win = devkit.makeWindow(winConfig);
	var b1 = Titanium.UI.createButton({
		title:'Press Me',
		height:40,
		width:200,
		top:10
	});
	win.add(b1);
	b1.addEventListener('click', function(){
		var winConfig = {
			backgroundColor:'black',
			navBarHidden:true,
			fullscreen:false
		};
		var wPage = myt.page1Win(winConfig);
		wPage.open();
	});
	return win;
};
{code}

{code:title=framework.js|borderStyle=solid}
if (typeof(devkit) === 'undefined'){
	devkit = {};	
}
devkit.makeWindow=function(a){
	var win = Ti.UI.createWindow(a);
	win.orientationModes = [
		Ti.UI.PORTRAIT,
		Ti.UI.UPSIDE_PORTRAIT
	];
	return win;	
};
{code}

