{code:JavaScript}
var win = Ti.UI.currentWindow;

var searchBar = Ti.UI.createSearchBar({
	top: 10,
	left: 10,
	width: 200,
	height: 50,
});


win.add(searchBar);

	var l1 = Ti.UI.createLabel ({
		text: 'You add me!',
		backgroundColor: 'red',
		width: 40,
		height: 20,
		left: 30,
		top: 5
	});

searchBar.add(l1);
{code}
