fields.description:
h4. Expected Behavior
Show the full toolbar even when changing the device orientation

h4. Actual Behavior
The toolbar is partially hidden on landscape orientation. Steps to reproduce the bug:
- click on the inputText
- Turn the device to landscape
At this point the 50% of the toolbar is hidden behind the keyboard

h4. Test Case
{code:title=app.js}
Titanium.UI.setBackgroundColor('#000');

// create tab group
var tabGroup = Titanium.UI.createTabGroup();
//
// create base UI tab and root window
//
var win1 = Titanium.UI.createWindow({
	title:'Tab 1',
	backgroundColor:'#fff',
	navBarHidden:false
});


win1.orientationModes=[
     Titanium.UI.PORTRAIT,
     Titanium.UI.LANDSCAPE_LEFT,
     Titanium.UI.LANDSCAPE_RIGHT];
var flexSpace = Titanium.UI.createButton({
		systemButton:Titanium.UI.iPhone.SystemButton.FLEXIBLE_SPACE
	});
	var done=Titanium.UI.createButton({
    	systemButton:Titanium.UI.iPhone.SystemButton.DONE,
    	backgroundColor:'#44648E',
		backgroundImage:'none'
	});
		var textField = Titanium.UI.createTextField({
				color:'#385487',
				right:'3.1%',
				left:'3.1%',
				top:10,
				borderStyle:Titanium.UI.INPUT_BORDERSTYLE_NONE,
				border:0,
				height:44,
				borderColor: '#000000',
				backgroundColor: 'transparent',
				font:{fontSize:17,fontFamily:'Helvetica Neue'},
				returnKeyType:Titanium.UI.RETURNKEY_DONE,
				keyboardType:Ti.UI.KEYBOARD_NUMBER_PAD,
				keyboardToolbar:[flexSpace,flexSpace,done],
			});

done.addEventListener('click',function(e){
		textField.blur()
});

win1.add(textField);




var tab1 = Titanium.UI.createTab({
	icon:'KS_nav_views.png',
	title:'Tab 1',
	window:win1
});

// create controls tab and root window
//
var win2 = Titanium.UI.createWindow({
	title:'Tab 2',
	backgroundColor:'blue',
	navBarHidden:false
});

var tab2 = Titanium.UI.createTab({
	icon:'KS_nav_ui.png',
	title:'Tab 2',
	window:win2
});


tabGroup.addTab(tab1);
tabGroup.addTab(tab2);

// open tab group
tabGroup.open();
{code}
